package se.attebrant.aoc2017;

public class Advent0101 {

  public static void main(String[] args) {
    test("1122");
    test("1111");
    test("1234");
    test("91212129");
    test(
        "");
    test(
        "");
  }

  public static void test(String digits) {
    System.out.println(digits + " -> ");
    System.out.println(sumitup(digits));
  }

  public static Integer sumitup(String digits) {
    char previousDigit = '-';
    Integer sum = 0;
    for (char digit : digits.toCharArray()) {
      if (digit == previousDigit) {
        sum = sum + Integer.parseInt(digit + "");
      }
      previousDigit = digit;
    }
    char firstDigit = digits.charAt(0);
    if (firstDigit == previousDigit) {
      sum = sum + Integer.parseInt(firstDigit + "");
    }
    return sum;
  }
}
